function [prediction] = calculatePrediction (norm, k, n)  if(n == 0)    prediction = [norm(:,1) .* k(1,1) + norm(:,2) .* k(1,2) + norm(:,3) .* k(1,3) + norm(:,4) .* k(1,4) + norm(:,5) .* k(1,5) + norm(:,6) .* k(1,6)];  else  prediction = [norm(n,1) .* k(1,1) + norm(n,2) .* k(1,2) + norm(n,3) .* k(1,3) + norm(n,4) .* k(1,4) + norm(n,5) .* k(1,5) + norm(n,6) .* k(1,6)];  endif  endfunction 